# emits ~ 1.6GB (when written to a file)
get '/stream' do
  response['Content-Length'] = nil
  response['Content-Type'] = 'text/xml'

  stream do |out|
    out << "<items>\n"
    300000.times { out << Item.call}
    out << "</items>"
  end
end
